<?php    namespace common\components;    use cms\models\TbSettings;    class Api extends \yii\web\Controller    {        public $setting;        public $enableCsrfValidation = false;        public function init()        {            parent::init();            if($this->setting == null){                $cache = \Yii::$app->cache;                $key   = 'Cache:Api-' . date('m-Y');                if (($data = $cache->get($key)) === false) {                    $tbSetting = TbSettings::find()->select('name,value')->where(['name' => ['CNY', 'hotline']])->asArray()->limit(2)->all();                    if ($tbSetting) {                        foreach ($tbSetting as $item) {                            $data[$item['name']] = $item['value'];                        }                    }                    $cache->set($key, $data, \Yii::$app->params['CACHE_TIME']['HOUR']);                }                $this->setting = $data;            }        }    }